import React from "react"
import List from "./List"

export default function Groups(props) {

    function addListItem(inputValue) {
        const newListItem = {
            key: list.length,
            id: list.length + 1,
            value: inputValue,
        }
        props.onAddListItem(newListItem)
    }

    function removeListItem(itemToRemove) {
        setList(prevList => {
            return prevList.filter(item => item.id !== itemToRemove.id)
        })
    }
    function handleChangeTitle(e, groupId) {
        const value = e.currentTarget.value
        props.onChangeTitle(groupId, value)
    }

    function onKeyDown(e) {
        if (e.key === "Enter" || e.key === "Escape") {
            e.preventDefault()
            props.setSelectedGroupId(null)
        }
    }

    const groupElements = props.groups.map((group) => {
        return group.id === props.selectedGroupId ? (
            <div className="groupContainer">
                <div className="group">
                    <input
                        key={group.id}
                        onKeyDown={onKeyDown}
                        onChange={(e) => handleChangeTitle(e, group.id)}
                    />
                    <button onClick={() => props.setSelectedGroupId(null)}>save</button>
                </div>
                <List />
            </div>
        ) :
            <div className="groupContainer">
                <div className="group">
                    <h3 onClick={() => props.setSelectedGroupId(group.id)}>{group.title}</h3>
                    <button onClick={() => props.deleteGroup(group)}>Delete</button >
                </div>
                <List />
            </div>
    })


    return (
        <div className="toDo">
            <button className="addNewGroupButton" onClick={props.addNewGroup}>Add new Group</button>
            {groupElements}
        </div>
    )
}
