import React from 'react';
import ToDoList from './ToDoList';


function App() {
  const [groups, setGroups] = React.useState([])
  const [selectedGroupId, setSelectedGroupId] = React.useState()

  function addNewGroup() {
    const newGroup = {
      title: 'Untitled Group',
      id: groups.length,
      list: [],
    }
    setGroups(prevGroups => [newGroup, ...prevGroups])
    setSelectedGroupId(newGroup.id)
  }

  function selectGroup(id) {
    setSelectedGroupId(id)
  }

  function deleteGroup(groupToRemove) {
    setGroups(prevGroups => {
      return prevGroups.filter(group => group !== groupToRemove)
    })
  }

  function handleTitleChange(e, groupId) {
    e.preventDefault();
    setGroups(prevGroups => {
      const groupToChange = prevGroups.find((group) => group.id === groupId)
      groupToChange.title = e.target.value
      return prevGroups
    })
  }

  function handleAddingListItems(e, groupId) {
    e.preventDefault();
    setGroups(prevGroups => {
      const groupToChange = prevGroups.find((group) => group.id === groupId)
      groupToChange.list = [e.target.value, ...groupToChange.list]
      return prevGroups
    })
  }


  return (
    <div className="App">
      <ToDoList
        deleteGroup={deleteGroup}
        setSelectedGroupId={selectGroup}
        selectedGroupId={selectedGroupId}
        addNewGroup={addNewGroup}
        changeTitle={handleTitleChange}
        groups={groups}
      />
    </div>
  );
}

export default App;
