import React from "react";


export default function List(props){
    const [list, setList] = React.useState([])
    const [inputValue, setInputValue] = React.useState('')

    function addListItem(e) {
        e.preventDefault()
        const newListItem = {
            key: list.length,
            id: list.length +1,
            value: {inputValue},
        }
        setList(prevList => {
            return [newListItem, ...prevList]
        })
        setInputValue('')
      }

      function completeListItem(itemToRemove) {
        setList(prevList => {
          return prevList.filter(item => item.id !== itemToRemove.id)
        })
      }

      const listItems = list.map(item =>{
        return(
            <div>
                {item}
                <button onClick={completeListItem(item)}>complete</button>
            </div>
        )
      })

    return (
        <div>
            <div>
                <form action="">
                    <input 
                        value={inputValue}
                        onChange={(e)=>{
                            setInputValue(e.currentTarget.value)
                        }} 
                        type="text" 
                    />
                    <button onClick={addListItem}>+</button>
                </form>
            </div>
            {listItems}
        </div>
    )
}
