  //     return [
  //       prevGroup.title = e.target.value,
  //       ...prevGroup
  //     ]
  //   })
  // }

  return (
    <div className="App">
      <Header
        addNewGroup={addNewGroup}
      />
      <ToDoList
        groups={groups}
      />
    </div>
  );
}

export default App;
