import { setSelectionRange } from "@testing-library/user-event/dist/utils"
import React from "react"


export default function ToDoList(props) {

    function onKeyDown(e) {
        if (e.key === "Enter" || e.key === "Escape") {
            e.preventDefault()
            props.setSelectedGroupId(null)
            return
        }
    }


    const groupElements = props.groups.map((group, index) => group.id === props.selectedGroupId ? (
        <div>
            <input
                key={group.id}
                onKeyDown={onKeyDown}
                onChange={(e) => props.onChange(e, group.id)}
            />
            <button onClick={() => props.setSelectedGroupId(null)}>save</button>
        </div>
    ) :
        <div>
            <h3 onClick={() => props.setSelectedGroupId(group.id)}>{group.title}</h3>
            <button>Delete</button >
        </div >)
    return (

        <section>
            <button onClick={props.addNewGroup}>Add new Group</button>
            {groupElements}
        </section>
    )
}
